#include <stdio.h>

int main() {

  long long n3 = 7316717653133;
  long long n2 = 3167176531330;
  long long n1 = 1671765313306;
  long long n = 6717653133062;

  long double max = (long double) n * n1 * n2 * n3;

  char number[] = "";

  // start at 16 offset as first numbers are hard coded
  int curr = -1;
  for (int i = 16 ; i < strlen(number) ; i++) {
    
    // transfer over numbers
    n3 = n2;
    n2 = n1;
    n1 = n;

    // clear out 12th place and read in first digit
    n = (n - (n - (n % 1000000000000))) * 10;
    n = (n - (n % 10)) + (number[i] - '0');

    if ((long double) n * n1 * n2 * n3 > max)
      max = (long double) n * n1 * n2 * n3;
  }

  printf("%Lf \n", max);

	return 0;
}
